import { Component, inject, OnInit } from '@angular/core';
import { NgForOf } from '@angular/common';
import { HttpClient, HttpClientModule } from '@angular/common/http';
import { AddMenuItemDialogComponent } from '../add-menu-item/add-menu-item.component';
import { MatDialog } from '@angular/material/dialog';
import { MatButton, MatButtonModule } from '@angular/material/button';
import { environment } from '../../../environments/environment';
import { TransactionService } from '../../services/transaction.service';
import { MatSnackBar } from '@angular/material/snack-bar';

export interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  image: string;
  pointsPrice: number;
  category: string;
  available: boolean;
}

@Component({
  selector: 'app-menu',
  standalone: true,
  templateUrl: './menu-page.component.html',
  imports: [
    NgForOf,
    HttpClientModule,
    MatButtonModule
  ],
  styleUrls: ['./menu-page.component.css']
})
export class MenuComponent implements OnInit {
  menuItems: Product[] = [];

  constructor(protected httpclient: HttpClient,
              protected dialog: MatDialog,
              protected transactionService: TransactionService) {

  }

  userId = "";
  private _snackBar = inject(MatSnackBar);

  private apiUrl = environment.apiUrl;

  ngOnInit(): void {

    this.fetchData()
    this.userId = localStorage.getItem("userId") || "";
    console.log("Got user ", this.userId)

  }

  addItem() {


  }

  deleteItem(id: number) {
    this.httpclient.delete(`${this.apiUrl}/product/` + id, {}).subscribe(() => {
      this.fetchData()
    })


  }
  fetchData() {
    this.httpclient.get<Product[]>(`${this.apiUrl}/product`).subscribe(data => {
      this.menuItems = data;
    })
  }
  openAddDialog() {
    const dialogRef = this.dialog.open(AddMenuItemDialogComponent, {
      width: '400px'
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        console.log('New Menu Item:', result);
        const product: Product = {
          id: -1,
          name: result.name,
          category: result.category,
          description: result.description,
          price: result.price,
          pointsPrice: result.pointsPrice,
          image: "",
          available: true
        }

        this.httpclient.post<Product>(`${this.apiUrl}/product`, product)
          .subscribe(newProduct => {
            this.menuItems.push(newProduct)
          })


      }
    });
  }

  updateItem(product: Product) {

    const dialogRef = this.dialog.open(AddMenuItemDialogComponent, {
      width: '400px',
      data: product
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        const updateProduct: Product = {
          id: product.id,
          name: result.name,
          category: result.category,
          description: result.description,
          price: result.price,
          pointsPrice: result.pointsPrice,
          image: "",
          available: true
        }

        this.httpclient.put<Product>(`${this.apiUrl}/product`, updateProduct)
          .subscribe(newProduct => {
            this.fetchData()
          })


      }
    });


  }

  buyItem(product: Product) {
    console.log("buy product")
    this.transactionService.buyProduct(product.id, this.userId).subscribe({

      next: (response) => {
        this._snackBar.open('Transaction Finished', '', {
          duration: 3000,
          panelClass: ['blue-snackbar']
        });
      },
      error: (err) => {
        this._snackBar.open('Not enough balance', "", {
          duration: 3000,
          panelClass: ['red-snackbar']
        });
      }
    })
  }

  // Added this method to MenuComponent class
  getItemImage(item: Product): string {
    // Return real images for existing items, placeholder for new ones
    const imageMap: { [key: string]: string } = {
      'Coffee': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRssNMQEBevkHRpnHEN47dICaxVP9czrKJ9dA&s',
      'Campus Cripsy Sandwich': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS-VWN5WInW0wu58pbmI_oMwpQSdRZogTmOzw&s',
      'Pizza Solo Slice': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSQxAC3KNGMhMz0Cmxj3wXZoEa7UYY49tHlQgjm3pqeIaf2rcEIprqJP0iuCo4SAQjetng&usqp=CAU', // Alternative spelling
      'Hydro Hit H20': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT2oicIoFmKdxQstLSMjOhFmgAiVkhQLjs5Rg&s',
      'Tufahija': 'data:image/jpeg;base64,',
      'Fresh Fix Salad': 'data:image/jpeg;base64,'
    };

    // If menu item has an image URL, use it
    if (item.image && item.image.trim() !== '') {
      return item.image;
    }

    // If it's a known menu item, use the mapped image
    if (imageMap[item.name]) {
      return imageMap[item.name];
    }

    // For new items without images, use placeholder
    return this.getPlaceholderImage();
  }

  getPlaceholderImage(): string {
    return 'data:image/png;base64,';
  }

  onImageError(event: any): void {
    // Fallback to placeholder if image fails to load
    event.target.src = this.getPlaceholderImage();
  }

}
